SELECT ls.person FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE r.area_name = 'Gujarat' AND l.name = 'Gujarati' AND t.name = 'Total';
SELECT r.area_name, SUM(ls.person) AS odia_speakers FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Odia' AND t.name = 'Total' GROUP BY r.area_name ORDER BY odia_speakers DESC LIMIT 1;
SELECT ls.person FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE r.area_name = 'Assam' AND l.name = 'Assamese' AND t.name = 'Total';
SELECT ls.person FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE r.area_name = 'Karnataka' AND l.name = 'Kannada' AND t.name = 'Rural';
SELECT r.area_name, SUM(ls.person) AS konkani_speakers FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Konkani' AND t.name = 'Total' GROUP BY r.area_name ORDER BY konkani_speakers DESC LIMIT 1;
SELECT SUM(rs.f_lit) AS literate_muslim_women FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Muslim' AND r.area_name = 'India' AND t.name = 'Total';
SELECT r.area_name, SUM(rs.f_lit) AS literate_hindu_women FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Hindu' AND t.name = 'Total' GROUP BY r.area_name ORDER BY literate_hindu_women DESC LIMIT 1;
SELECT rs.tot_work_p FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Kerala' AND rel.religion_name = 'Christian' AND t.name = 'Total';
SELECT rs.p_ill FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Rajasthan' AND rel.religion_name = 'Jain' AND t.name = 'Total';
SELECT rel.religion_name, (SUM(rs.f_lit) * 100.0 / NULLIF(SUM(rs.tot_f), 0)) as female_literacy_rate FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Uttar Pradesh' AND t.name = 'Total' GROUP BY rel.religion_name ORDER BY female_literacy_rate ASC LIMIT 1;
SELECT rs.tot_m FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'Punjab' AND rel.religion_name = 'Sikh' AND t.name = 'Rural';
SELECT r.area_name, SUM(rs.tot_p) AS urban_buddhists FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE rel.religion_name = 'Buddhist' AND t.name = 'Urban' GROUP BY r.area_name ORDER BY urban_buddhists DESC LIMIT 1;
