### SQL
SELECT r.area_name, SUM(ls.person) AS konkani_speakers FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Konkani' AND t.name = 'Total' GROUP BY r.area_name ORDER BY konkani_speakers DESC LIMIT 1;

