### SQL
SELECT SUM(ls.person) AS sanskrit_speakers FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE l.name = 'Sanskrit' AND r.area_name = 'India' AND t.name = 'Total';

