### SQL
SELECT t.name, ls.person FROM language_stats ls JOIN regions r ON ls.state = r.state JOIN languages l ON ls.language_id = l.id JOIN tru t ON ls.tru_id = t.id WHERE r.area_name = 'Madhya Pradesh' AND l.name = 'Hindi' AND t.name IN ('Rural', 'Urban');

