### SQL
SELECT rel.religion_name, SUM(rs.tot_p) AS rural_population FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'India' AND t.name = 'Rural' GROUP BY rel.religion_name ORDER BY rural_population DESC LIMIT 1;

