### SQL
SELECT rel.religion_name, (SUM(CASE WHEN t.name = 'Urban' THEN rs.tot_p ELSE 0 END) * 100.0 / NULLIF(SUM(rs.tot_p), 0)) AS urbanization_rate FROM religion_stats rs JOIN regions r ON rs.state = r.state JOIN religions rel ON rs.religion_id = rel.id JOIN tru t ON rs.tru_id = t.id WHERE r.area_name = 'India' GROUP BY rel.religion_name ORDER BY urbanization_rate DESC LIMIT 1;

